mapper.select:
select *
from document
where catId = #{catId}
  and hidden = false
order by touch desc;

mapper.update:
update document
set hidden = true
where docId = #{docId};

update document
set hidden = false
where docId = #{docId};

update document
set catId   = #{catId},
    title   = #{title},
    `desc`  = #{desc},
    content = #{content}
where docId = #{docId};

update document
set touch = now()
where docId = #{docId};
